CREATE VIEW public.extraction_statistics AS
 SELECT cm.extraction_session_id,
    cm.extractor_version,
    cm.llm_model,
    count(*) AS concepts_extracted,
    avg(cm.llm_confidence) AS avg_llm_confidence,
    avg(c.confidence_score) AS avg_final_confidence,
    count(
        CASE
            WHEN ((c.status)::text = 'approved'::text) THEN 1
            ELSE NULL::integer
        END) AS approved_count,
    count(
        CASE
            WHEN ((c.status)::text = 'rejected'::text) THEN 1
            ELSE NULL::integer
        END) AS rejected_count,
    min(cm.extraction_timestamp) AS session_start,
    max(cm.extraction_timestamp) AS session_end
   FROM (public.candidate_metadata cm
     JOIN public.concepts c ON ((cm.concept_id = c.id)))
  GROUP BY cm.extraction_session_id, cm.extractor_version, cm.llm_model
  ORDER BY (min(cm.extraction_timestamp)) DESC;


