rdf:resource="http://purl.obolibrary.org/obo/IAO_0000030"/>\n        <obo:IAO_0000111 xml:lang="en">figure</obo:IAO_0000111>\n        <obo:IAO_0000112 xml:lang="en">Any picture, diagram or table</obo:IAO_0000112>\n        <obo:IAO_0000114 rdf:resource="http://purl.obolibrary.org/obo/IAO_0000120"/>\n        <obo:IAO_0000115 xml:lang="en">An information content entity consisting of a two dimensional arrangement of information content entities such that the arrangement itself is about something.</obo:IAO_0000115>\n        <obo:IAO_0000117 xml:lang="en">PERSON: Lawrence Hunter</obo:IAO_0000117>\n        <rdfs:label xml:lang="en">figure</rdfs:label>\n    </owl:Class>\n    \n\n\n    <!-- http://purl.obolibrary.org/obo/IAO_0000309 -->\n\n    <owl:Class rdf:about="http://purl.obolibrary.org/obo/IAO_0000309">\n        <rdfs:subClassOf rdf:resource="http://purl.obolibrary.org/obo/IAO_0000308"/>\n        <obo:IAO_0000111 xml:lang="en">diagram</obo:IAO_0000111>\n        <obo:IAO_0000112 xml:lang="en">A molecular structure ribbon cartoon showing helices, turns and sheets and their relations to each other in space.</obo:IAO_0000112>\n        <obo:IAO_0000114 rdf:resource="http://purl.obolibrary.org/obo/IAO_0000120"/>\n        <obo:IAO_0000115 xml:lang="en">A figure that expresses one or more propositions</obo:IAO_0000115>\n        <obo:IAO_0000117 xml:lang="en">PERSON: Lawrence Hunter</obo:IAO_0000117>\n        <rdfs:label xml:lang="en">diagram</rdfs:label>\n    </owl:Class>\n    \n\n\n    <!-- http://purl.obolibrary.org/obo/IAO_0000310 -->\n\n    <owl:Class rdf:about="http://purl.obolibrary.org/obo/IAO_0000310">\n        <rdfs:subClassOf rdf:resource="http://purl.obolibrary.org/obo/IAO_0000030"/>\n        <obo:IAO_0000111 xml:lang="en">document</obo:IAO_0000111>\n        <obo:IAO_0000112 xml:lang="en">A journal article, patent application, laboratory notebook, or a book</obo:IAO_0000112>\n        <obo:IAO_0000114 rdf:resource="http://purl.obolibrary.org/obo/IAO_0000120"/>\n     